def run(data, marker_len):
    print(f"Using data {data[:10]}...")

    for i in range(marker_len-1, len(data)):
        chars = set()
        for j in range(i-marker_len+1, i+1):
            chars.add(data[j])
        if len(chars) == marker_len:
            print(f"Must process {i+1} characters")
            return


print(f"========================================")
